import React, { useState } from 'react';
import './JsonEditor.css';

const EditModal = ({ data, onClose, onSave, URL }) => {
    const [formData, setFormData] = useState({ ...data });
    const [error, setError] = useState(null);
    const [loading, setLoading] = useState(false);

    // Manejar cambios en los campos del formulario
    const handleChange = (e) => {
        const { name, value } = e.target;
        setFormData({ ...formData, [name]: value });
    };

    // Enviar los datos actualizados al backend
    const handleSubmit = async (e) => {
        e.preventDefault();
        setLoading(true);
        setError(null);

        try {
            // Enviar los datos actualizados al backend mediante una solicitud PUT
            const response = await fetch(`${URL}student/editar-estudiante/${formData.DNI}`, {
                method: 'PUT',
                headers: {
                    'Content-Type': 'application/json',
                },
                body: JSON.stringify(formData),
            });

            if (!response.ok) {
                throw new Error('Error al actualizar el estudiante');
            }

            // Llamar a la función `onSave` para notificar al padre que los datos han sido guardados
            onSave(formData);

            // Cerrar el modal
            onClose();
        } catch (error) {
            setError(error.message);
        } finally {
            setLoading(false);
        }
    };

    return (
        <div className="modal-overlay">
            <div className="modal-content">
                <h2>Editar Información</h2>
                {error && <p className="error-message">{error}</p>}
                <form onSubmit={handleSubmit}>
                    {Object.keys(formData).map(
                        (key) =>
                            key !== 'id' && (
                                <div className="form-group" key={key}>
                                    <label>{key.replace(/_/g, ' ')}</label>
                                    <input
                                        type="text"
                                        name={key}
                                        value={formData[key] ?? ''}
                                        onChange={handleChange}
                                    />
                                </div>
                            )
                    )}
                    <div className="modal-actions">
                        <button type="submit" className="save-btn" disabled={loading}>
                            {loading ? 'Guardando...' : 'Guardar'}
                        </button>
                        <button type="button" className="close-btn" onClick={onClose}>
                            Cerrar
                        </button>
                    </div>
                </form>
            </div>
        </div>
    );
};

export default EditModal;
