import { useState, useEffect } from "react";
import './app.css';

export default function StudentData() {
    const [data, setData] = useState([]);
    const [error, setError] = useState(null);
    const [loading, setLoading] = useState(true);
    const [NOMBRE, setNOMBRE] = useState("");
    const [currentPage, setCurrentPage] = useState(1);
    const studentsPerPage = 40;

    // Obtener todos los estudiantes
    const fetchData = async () => {
        try {
            const response = await fetch("http://localhost:5000/student/");
            if (!response.ok) throw new Error("Error al obtener los datos");
            const result = await response.json();
            setData(result);
        } catch (error) {
            setError(error.message);
        } finally {
            setLoading(false);
        }
    };

    // Filtrar por nombre
    const fetchFiltro = async (nombre) => {
        try {
            const response = await fetch(`http://localhost:5000/student/BUSCAR?apellidosNombres=${nombre}`);
            if (!response.ok) throw new Error("Error al obtener los datos");
            const result = await response.json();
            setData(result);
        } catch (error) {
            setError(error.message);
        }
    };

    useEffect(() => {
        fetchData();
    }, []);

    const handleChange = (event) => {
        const nombre = event.target.value;
        setNOMBRE(nombre);
        nombre.trim() ? fetchFiltro(nombre) : fetchData();
        setCurrentPage(1);
    };

    // Paginación
    const indexOfLastStudent = currentPage * studentsPerPage;
    const indexOfFirstStudent = indexOfLastStudent - studentsPerPage;
    const currentStudents = data.slice(indexOfFirstStudent, indexOfLastStudent);
    const totalPages = Math.ceil(data.length / studentsPerPage);

    const handlePageChange = (page) => setCurrentPage(page);

    const handleView = (student) => alert(`Ver detalles de: ${student.APELLIDOS_NOMBRES}`);
    const handleDelete = (id) => alert(`Eliminar estudiante con ID: ${id}`);

    if (loading) return <p>Cargando datos...</p>;
    if (error) return <p style={{ color: "red" }}>Error: {error}</p>;

    return (
        <div className="container">
    <h1>Datos del Estudiante</h1>
    <div className="flex gap-2 mb-4">
        <input
            type="text"
            value={NOMBRE}
            onChange={handleChange}
            placeholder="Escribe el nombre..."
            className="input"
        />
        <button className="button button-primary" onClick={() => alert("Agregar nuevo estudiante")}>
            Agregar
        </button>
    </div>

    <ul className="student-list">
        {currentStudents.map((student) => (
            <li key={student.id} className="student-item">
                <span>{student.GR} - {student.DNI} - {student.APELLIDOS_NOMBRES} - {student.SITUACIÓN_MATRICULA}</span>
                <div className="flex gap-2">
                    <button className="button button-primary" onClick={() => handleView(student)}>
                        Ver
                    </button>
                    <button className="button button-danger" onClick={() => handleDelete(student.id)}>
                        Eliminar
                    </button>
                </div>
            </li>
        ))}
    </ul>

    <div className="pagination">
        {[...Array(totalPages)].map((_, index) => (
            <button
                key={index + 1}
                className={`pagination-button ${currentPage === index + 1 ? 'active' : ''}`}
                onClick={() => handlePageChange(index + 1)}
            >
                {index + 1}
            </button>
        ))}
    </div>
</div>

    );
}
