import { useState, useEffect } from "react";

export default function StudentData() {
    const [data, setData] = useState(null);
    const [error, setError] = useState(null);
    const [loading, setLoading] = useState(true);
    const [DNI, setDNI] = useState("");
    const [NOMBRE, setNOMBRE] = useState("");

    useEffect(() => {
        const fetchData = async () => {
            try {
                const response = await fetch("http://localhost:5000/student/");
                if (!response.ok) throw new Error("Error al obtener los datos");
                const result = await response.json();
                setData(result);
            } catch (error) {
                setError(error.message);
            } finally {
                setLoading(false);
            }
        };

        fetchData();
    }, []);

    const fetchfiltro = async (nombre) => {
        try {
            const response = await fetch(`http://localhost:5000/student/BUSCAR?apellidosNombres=${nombre}`);
            if (!response.ok) throw new Error("Error al obtener los datos");
            const result = await response.json();
            setData(result);
        } catch (error) {
            setError(error.message);
        }
    };

    const handleChange = (event) => {
        const nombre = event.target.value;
        setNOMBRE(nombre);
        fetchfiltro(nombre); // Usar el valor directo evita el desfase del estado
    };
    function Error(){
        if (error) return <p>Error: {error}</p>;
    }
    if (loading) return <p>Cargando datos...</p>;
    
    if (!data) return <p>No hay datos disponibles.</p>;

    return (
        <div>
            <h1>Datos del estudiante</h1>
            <input
                type="text"
                value={NOMBRE}
                onChange={handleChange}
                placeholder="Escribe el nombre..."
            />
            <ul>
                {data.map((student) => (
                    <li key={student.id}>
                        <Error />
                        {student.GR} - {student.DNI} - {student.APELLIDOS_NOMBRES}
                    </li>
                ))}
            </ul>
        </div>
    );
}
