import { useState, useEffect } from "react";

export default function StudentData() {
    const [data, setData] = useState([]);
    const [error, setError] = useState(null);
    const [loading, setLoading] = useState(true);
    const [NOMBRE, setNOMBRE] = useState("");

    // Función para obtener todos los datos
    const fetchData = async () => {
        try {
            const response = await fetch("http://localhost:5000/student/");
            if (!response.ok) throw new Error("Error al obtener los datos");
            const result = await response.json();
            setData(result);
        } catch (error) {
            setError(error.message);
        } finally {
            setLoading(false);
        }
    };

    // Cargar todos los datos al inicio
    useEffect(() => {
        fetchData();
    }, []);

    // Función para filtrar por nombre
    const fetchFiltro = async (nombre) => {
        try {
            const response = await fetch(`http://localhost:5000/student/BUSCAR?apellidosNombres=${nombre}`);
            if (!response.ok) throw new Error("Error al obtener los datos");
            const result = await response.json();
            setData(result);
        } catch (error) {
            setError(error.message);
        }
    };

    // Manejo del cambio en el input
    const handleChange = (event) => {
        const nombre = event.target.value;
        setNOMBRE(nombre);

        if (nombre.trim() !== "") {
            fetchFiltro(nombre);
        } else {
            fetchData(); // Carga todos los datos si el input está vacío
        }
    };

    if (loading) return <p>Cargando datos...</p>;
    if (error) return <p style={{ color: "red" }}>Error: {error}</p>;
    if (!data) return <p>No hay datos disponibles.</p>;

    return (
        <div>
            <h1>Datos del estudiante</h1>
            <input
                type="text"
                value={NOMBRE}
                onChange={handleChange}
                placeholder="Escribe el nombre..."
            />
            <ul>
                {data.map((student) => (
                    <li key={student.id}>
                        {student.GR} - {student.DNI} - {student.APELLIDOS_NOMBRES}
                    </li>
                ))}
            </ul>
        </div>
    );
}
