import { useState, useEffect } from "react";
import '../css/app.css';
import EditModal from "../components/model";
import StudentTable from "./StudentTable";
import Pagination from "./Pagination";

export default function StudentData({ URL }) {
    const [data, setData] = useState([]);
    const [error, setError] = useState(null);
    const [loading, setLoading] = useState(true);
    const [nombre, setNombre] = useState("");
    const [currentPage, setCurrentPage] = useState(1);
    const [selectedStudent, setSelectedStudent] = useState(null);
    const [isModalOpen, setIsModalOpen] = useState(false);
    const studentsPerPage = 40;

    // Obtener todos los estudiantes
    const fetchData = async () => {
        setLoading(true);
        try {
            const response = await fetch(`${URL}student/`);
            if (!response.ok) throw new Error("Error al obtener los datos");
            const result = await response.json();
            setData(result);
        } catch (error) {
            setError(error.message);
        } finally {
            setLoading(false);
        }
    };

    useEffect(() => {
        fetchData();
    }, [URL]);

    const handleChange = (event) => {
        const nombre = event.target.value;
        setNombre(nombre);
        nombre.trim() ? fetchFiltro(nombre) : fetchData();
        setCurrentPage(1);
    };

    const handleView = (student) => {
        setSelectedStudent(student);
        setIsModalOpen(true);
    };

    const handleCloseModal = () => {
        setIsModalOpen(false);
        setSelectedStudent(null);
    };

    const handleUpdate = (updatedStudent) => {
        const updatedData = data.map((student) =>
            student.id === updatedStudent.id ? updatedStudent : student
        );
        setData(updatedData);
    };

    const handleDelete = (id) => alert(`Eliminar estudiante con ID: ${id}`);

    

    if (loading) return <p>Cargando datos...</p>;
    if (error) return <p style={{ color: "red" }}>Error: {error}</p>;

    const indexOfLastStudent = currentPage * studentsPerPage;
    const indexOfFirstStudent = indexOfLastStudent - studentsPerPage;
    const currentStudents = data.slice(indexOfFirstStudent, indexOfLastStudent);
    const totalPages = Math.ceil(data.length / studentsPerPage);

    return (
        <div className="container">
            <h1>Datos del Estudiante</h1>
            <div className="flex gap-2 mb-4">
                <input
                    type="text"
                    value={nombre}
                    onChange={handleChange}
                    placeholder="Escribe el nombre..."
                    className="input"
                />
                <button className="button button-primary" onClick={() => alert("Agregar nuevo estudiante")}>Agregar</button>
                <button className="button button-primary" onClick={handleExportExcel}>Exportar EXCEL</button>
            </div>

            <StudentTable
                students={currentStudents}
                onView={handleView}
                onDelete={handleDelete}
            />

            <Pagination
                currentPage={currentPage}
                totalPages={totalPages}
                onPageChange={setCurrentPage}
            />

            {isModalOpen && selectedStudent && (
                <EditModal
                    data={selectedStudent}
                    onClose={handleCloseModal}
                    onSave={handleUpdate}
                    URL={URL}
                />
            )}
        </div>
    );
}
