import { useState, useEffect } from "react";
import './app.css';

export default function StudentData({ URL }) {
    const [data, setData] = useState([]);
    const [error, setError] = useState(null);
    const [loading, setLoading] = useState(true);
    const [NOMBRE, setNOMBRE] = useState("");
    const [currentPage, setCurrentPage] = useState(1);
    const [selectedStudent, setSelectedStudent] = useState(null);
    const studentsPerPage = 40;

    // Función para obtener todos los estudiantes
    const fetchData = async () => {
        setLoading(true);
        try {
            const response = await fetch(`${URL}student/`);
            if (!response.ok) throw new Error("Error al obtener los datos");
            const result = await response.json();
            setData(result);
        } catch (error) {
            setError(error.message);
        } finally {
            setLoading(false);
        }
    };

    // Función para buscar estudiantes por nombre
    const fetchFiltro = async (nombre) => {
        try {
            const response = await fetch(`${URL}student/BUSCAR?apellidosNombres=${nombre}`);
            if (!response.ok) throw new Error("Error al obtener los datos");
            const result = await response.json();
            setData(result);
        } catch (error) {
            setError(error.message);
        }
    };

    // Función para editar un estudiante
    const editStudent = async (dni, updatedData) => {
        try {
            const response = await fetch(`${URL}student/editar-estudiante/${dni}`, {
                method: 'PUT',
                headers: {
                    'Content-Type': 'application/json',
                },
                body: JSON.stringify(updatedData),
            });

            if (!response.ok) throw new Error("Error al actualizar el estudiante");

            // Actualizar el estado local con los datos actualizados
            setData((prevData) =>
                prevData.map((student) => (student.DNI === dni ? { ...student, ...updatedData } : student))
            );
        } catch (error) {
            setError(error.message);
        }
    };

    useEffect(() => {
        fetchData();
    }, [URL]);

    const handleChange = (event) => {
        const nombre = event.target.value;
        setNOMBRE(nombre);
        nombre.trim() ? fetchFiltro(nombre) : fetchData();
        setCurrentPage(1);
    };

    const indexOfLastStudent = currentPage * studentsPerPage;
    const indexOfFirstStudent = indexOfLastStudent - studentsPerPage;
    const currentStudents = data.slice(indexOfFirstStudent, indexOfLastStudent);
    const totalPages = Math.ceil(data.length / studentsPerPage);

    const handlePageChange = (page) => setCurrentPage(page);

    const handleView = (student) => setSelectedStudent(student);

    const handleSave = async () => {
        if (!selectedStudent) return;

        try {
            // Llamar al backend para actualizar el estudiante
            await editStudent(selectedStudent.DNI, selectedStudent);

            // Limpiar el estado del estudiante seleccionado
            setSelectedStudent(null);
        } catch (error) {
            setError(error.message);
        }
    };

    const handleDelete = (id) => alert(`Eliminar estudiante con ID: ${id}`);

    if (loading) return <p>Cargando datos...</p>;
    if (error) return <p style={{ color: "red" }}>Error: {error}</p>;

    return (
        <div className="container">
            <h1>Datos del Estudiante</h1>
            <div className="flex gap-2 mb-4">
                <input
                    type="text"
                    value={NOMBRE}
                    onChange={handleChange}
                    placeholder="Escribe el nombre..."
                    className="input"
                />
                <button className="button button-primary" onClick={() => alert("Agregar nuevo estudiante")}>
                    Agregar
                </button>
                <button className="button button-primary" onClick={() => alert("Exportar a EXCEL")}>
                    Exportar EXCEL
                </button>
            </div>
            <div className="container">
                <h2 className="text-xl font-bold mb-4">Lista de Estudiantes</h2>
                <table className="w-full border-collapse border border-gray-300">
                    <thead>
                        <tr className="bg-gray-200">
                            <th className="border p-2">Grado</th>
                            <th className="border p-2">DNI</th>
                            <th className="border p-2">Nombre Completo</th>
                            <th className="border p-2">Situación Matrícula</th>
                            <th className="border p-2">Acciones</th>
                        </tr>
                    </thead>
                    <tbody>
                        {currentStudents.map((student) => (
                            <tr key={student.id} className="border">
                                <td className="border p-2 text-center">{student.GR}</td>
                                <td className="border p-2 text-center">{student.DNI}</td>
                                <td className="border p-2 text-center">{student.APELLIDOS_NOMBRES}</td>
                                <td className="border p-2 text-center">{student.SITUACIÓN_MATRICULA}</td>
                                <td className="border p-2 text-center">
                                    <div className="flex justify-center gap-2">
                                        <button className="button button-primary" onClick={() => handleView(student)}>
                                            Ver
                                        </button>
                                        <button className="button button-danger" onClick={() => handleDelete(student.id)}>
                                            Eliminar
                                        </button>
                                    </div>
                                </td>
                            </tr>
                        ))}
                    </tbody>
                </table>
            </div>
            <div className="pagination">
                {[...Array(totalPages)].map((_, index) => (
                    <button
                        key={index + 1}
                        className={`pagination-button ${currentPage === index + 1 ? 'active' : ''}`}
                        onClick={() => handlePageChange(index + 1)}
                    >
                        {index + 1}
                    </button>
                ))}
            </div>
            {selectedStudent && (
                <div className="modal-overlay" style={{ overflowY: 'auto' }}>
                    <div className="modal-content" style={{ maxHeight: '90vh', overflowY: 'auto' }}>
                        <h2>Editar Información</h2>
                        {Object.keys(selectedStudent).map(
                            (key) =>
                                key !== 'id' && (
                                    <div className="form-group" key={key}>
                                        <label>{key.replace(/_/g, ' ')}</label>
                                        <input
                                            type="text"
                                            name={key}
                                            value={selectedStudent[key] ?? ''}
                                            onChange={(e) =>
                                                setSelectedStudent({ ...selectedStudent, [key]: e.target.value })
                                            }
                                        />
                                    </div>
                                )
                        )}
                        <div className="modal-actions">
                            <button className="save-btn" onClick={handleSave}>
                                Guardar
                            </button>
                            <button className="close-btn" onClick={() => setSelectedStudent(null)}>
                                Cerrar
                            </button>
                        </div>
                    </div>
                </div>
            )}
        </div>
    );
}
