import { useState, useEffect } from "react";
import { Button } from "@/components/ui/button";

export default function StudentData() {
    const [data, setData] = useState([]);
    const [error, setError] = useState(null);
    const [loading, setLoading] = useState(true);
    const [NOMBRE, setNOMBRE] = useState("");
    const [currentPage, setCurrentPage] = useState(1);
    const studentsPerPage = 40;

    // Obtener todos los estudiantes
    const fetchData = async () => {
        try {
            const response = await fetch("http://localhost:5000/student/");
            if (!response.ok) throw new Error("Error al obtener los datos");
            const result = await response.json();
            setData(result);
        } catch (error) {
            setError(error.message);
        } finally {
            setLoading(false);
        }
    };

    // Filtrar por nombre
    const fetchFiltro = async (nombre) => {
        try {
            const response = await fetch(`http://localhost:5000/student/BUSCAR?apellidosNombres=${nombre}`);
            if (!response.ok) throw new Error("Error al obtener los datos");
            const result = await response.json();
            setData(result);
        } catch (error) {
            setError(error.message);
        }
    };

    useEffect(() => {
        fetchData();
    }, []);

    const handleChange = (event) => {
        const nombre = event.target.value;
        setNOMBRE(nombre);
        nombre.trim() ? fetchFiltro(nombre) : fetchData();
        setCurrentPage(1);
    };

    // Paginación
    const indexOfLastStudent = currentPage * studentsPerPage;
    const indexOfFirstStudent = indexOfLastStudent - studentsPerPage;
    const currentStudents = data.slice(indexOfFirstStudent, indexOfLastStudent);
    const totalPages = Math.ceil(data.length / studentsPerPage);

    const handlePageChange = (page) => setCurrentPage(page);

    const handleView = (student) => alert(`Ver detalles de: ${student.APELLIDOS_NOMBRES}`);
    const handleDelete = (id) => alert(`Eliminar estudiante con ID: ${id}`);

    if (loading) return <p>Cargando datos...</p>;
    if (error) return <p style={{ color: "red" }}>Error: {error}</p>;

    return (
        <div className="p-4">
            <h1 className="text-2xl font-bold mb-4">Datos del Estudiante</h1>
            <div className="flex gap-2 mb-4">
                <input
                    type="text"
                    value={NOMBRE}
                    onChange={handleChange}
                    placeholder="Escribe el nombre..."
                    className="border rounded p-2 w-full"
                />
                <Button onClick={() => alert("Agregar nuevo estudiante")}>Agregar</Button>
            </div>

            <ul className="space-y-2">
                {currentStudents.map((student) => (
                    <li key={student.id} className="flex justify-between items-center border p-2 rounded">
                        <span>{student.GR} - {student.DNI} - {student.APELLIDOS_NOMBRES}</span>
                        <div className="flex gap-2">
                            <Button onClick={() => handleView(student)}>Ver</Button>
                            <Button onClick={() => handleDelete(student.id)} variant="destructive">Eliminar</Button>
                        </div>
                    </li>
                ))}
            </ul>

            {totalPages > 1 && (
                <div className="flex justify-center mt-4 gap-2">
                    {[...Array(totalPages)].map((_, index) => (
                        <Button
                            key={index + 1}
                            variant={currentPage === index + 1 ? "default" : "outline"}
                            onClick={() => handlePageChange(index + 1)}
                        >
                            {index + 1}
                        </Button>
                    ))}
                </div>
            )}
        </div>
    );
}
